import React from "react";

const HSVControls = ({ hsv, handleChange }) => {
  return (
    <div className="controls">
      {Object.keys(hsv).map((key) => (
        <div key={key} className="slider-container mb-3">
          <label>{key.toUpperCase()}</label>
          <input
            type="range"
            name={key}
            min={key.includes("h") ? 0 : 0}
            max={key.includes("h") ? 179 : 255}
            value={hsv[key]}
            onChange={handleChange}
            className="w-100"
          />
          <input
            type="number"
            name={key}
            min={key.includes("h") ? 0 : 0}
            max={key.includes("h") ? 179 : 255}
            value={hsv[key]}
            onChange={handleChange}
            className="w-100"
          />
        </div>
      ))}
    </div>
  );
};

export default HSVControls;
