import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [hsv, setHsv] = useState({
    h_min: 0,
    h_max: 179,
    s_min: 0,
    s_max: 255,
    v_min: 0,
    v_max: 255,
  });

  useEffect(() => {
    // Fetch initial HSV values
    fetch("http://194.12.173.218:5004/hsv")
      .then((response) => response.json())
      .then((data) => setHsv(data));
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setHsv((prevHsv) => ({
      ...prevHsv,
      [name]: parseInt(value),
    }));

    // Send updated HSV values to the server
    fetch("http://194.12.173.218:5004/hsv", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ [name]: parseInt(value) }),
    });
  };

  return (
    <div className="App">
      <h1>HSV Adjustment</h1>
      <div className="video-container">
        <img src="http://194.12.173.218:5004/video_feed" alt="OAK-D Stream" />
      </div>
      <div className="controls">
        {Object.keys(hsv).map((key) => (
          <div key={key} className="slider-container">
            <label>{key.toUpperCase()}</label>
            <input
              type="range"
              name={key}
              min={key.includes("h") ? 0 : 0}
              max={key.includes("h") ? 179 : 255}
              value={hsv[key]}
              onChange={handleChange}
            />
            <input
              type="number"
              name={key}
              min={key.includes("h") ? 0 : 0}
              max={key.includes("h") ? 179 : 255}
              value={hsv[key]}
              onChange={handleChange}
            />
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
