import React from "react";
import { Form, Row, Col } from "react-bootstrap";

const HSVControls = ({ hsv, handleChange }) => {
  return (
    <Form>
      {Object.keys(hsv).map((key) => (
        <Form.Group as={Row} key={key} className="mb-3 align-items-center">
          <Form.Label column sm="2" className="text-center text-uppercase">
            {key}
          </Form.Label>
          <Col sm="8">
            <Form.Control
              type="range"
              name={key}
              min={key.includes("h") ? 0 : 0}
              max={key.includes("h") ? 179 : 255}
              value={hsv[key]}
              onChange={handleChange}
              className="w-100"
            />
          </Col>
          <Col sm="2">
            <Form.Control
              type="number"
              name={key}
              min={key.includes("h") ? 0 : 0}
              max={key.includes("h") ? 179 : 255}
              value={hsv[key]}
              onChange={handleChange}
              className="text-center"
            />
          </Col>
        </Form.Group>
      ))}
    </Form>
  );
};

export default HSVControls;
